import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles.css';

export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        username,
        password,
      });

      const userData = res.data.user ? res.data.user : res.data;
      const normalizedUser = {
        ...userData,
        role: userData.role?.toLowerCase(),
      };

      localStorage.setItem('user', JSON.stringify(normalizedUser));

      if (normalizedUser.role === 'hr') navigate('/hr');
      else if (normalizedUser.role === 'interviewer') navigate('/interviewer');
      else if (normalizedUser.role === 'candidate') navigate('/candidate');
      else alert('Unknown role, cannot navigate.');
    } catch (err) {
      alert('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-card">
        <h1 className="login-title">Interview Manager</h1>
        <form className="login-form" onSubmit={handleLogin}>
          <div className="input-group">
            <label>Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="btn-login">Sign In</button>

          <p className="register-text">
            Donâ€™t have an account? <Link to="/register">Create one</Link>
          </p>

          <p className="hint">
            Try credentials: <b>hr1/password123</b>, <b>inter1/password123</b>, or <b>cand1/password123</b>
          </p>
        </form>
      </div>
    </div>
  );
}
