import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles.css';

export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        username,
        password,
      });
      localStorage.setItem('user', JSON.stringify(res.data));
      if (res.data.role === 'hr') navigate('/hr');
      else if (res.data.role === 'interviewer') navigate('/interviewer');
      else navigate('/candidate');
    } catch (err) {
      alert('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1 className="title">Interview Manager</h1>

        <form className="login-form" onSubmit={handleLogin}>
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="btn-primary">
            Sign In
          </button>
          <p className="register-text">
            Donâ€™t have an account? <Link to="/register">Create one</Link>
          </p>
          <p className="hint">
            Try credentials: <b>hr1/password123</b> or <b>inter1/password123</b> or <b>cand1/password123</b>
          </p>
        </form>
      </div>

      <div className="info-section">
        <div className="info-card">
          <h3>For HR Teams</h3>
          <p>Schedule interviews, manage candidates, and track progress.</p>
        </div>
        <div className="info-card">
          <h3>For Interviewers</h3>
          <p>View your schedule, conduct interviews, and submit feedback.</p>
        </div>
        <div className="info-card">
          <h3>For Candidates</h3>
          <p>Stay informed about interviews and receive timely updates.</p>
        </div>
      </div>
    </div>
  );
}
