import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import HRDashboard from './components/HRDashboard';
import CandidateDashboard from './components/CandidateDashboard';
import InterviewerDashboard from './components/InterviewerDashboard';
import { AuthProvider, useAuth } from './utils/auth';
import './styles.css';

function PrivateRoute({ children, roles }) {
  const { user } = useAuth();
  if (!user) return <Navigate to="/" />;
  if (roles && !roles.includes(user.role))
    return <div style={{ padding: 20 }}>Access denied</div>;
  return children;
}

function Layout({ children }) {
  const { user, logout } = useAuth();

  return (
    <div className="app-container">
      <header>
        <div className="logo">
          <img src="/logo.svg" alt="Logo" />
          <span>Interview Manager</span>
        </div>
        {user && (
          <div className="nav-actions">
            <span className="user-role">{user.role.toUpperCase()}</span>
            <button onClick={logout}>Logout</button>
          </div>
        )}
      </header>

      <main className="container">{children}</main>

      <footer>© 2025 Interview Manager — All rights reserved.</footer>
    </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Layout>
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route
              path="/hr"
              element={
                <PrivateRoute roles={['hr']}>
                  <HRDashboard />
                </PrivateRoute>
              }
            />
            <Route
              path="/candidate"
              element={
                <PrivateRoute roles={['candidate']}>
                  <CandidateDashboard />
                </PrivateRoute>
              }
            />
            <Route
              path="/interviewer"
              element={
                <PrivateRoute roles={['interviewer']}>
                  <InterviewerDashboard />
                </PrivateRoute>
              }
            />
          </Routes>
        </Layout>
      </BrowserRouter>
    </AuthProvider>
  );
}
