import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles.css';

export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        username,
        password,
      });
      localStorage.setItem('user', JSON.stringify(res.data));
      if (res.data.role === 'hr') navigate('/hr');
      else if (res.data.role === 'interviewer') navigate('/interviewer');
      else navigate('/candidate');
    } catch (err) {
      alert('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-hero">
        <h1 className="hero-title">Interview Manager</h1>
        <p className="hero-subtitle">
          A complete interview management platform for HR teams, interviewers, and candidates.
        </p>
      </div>

      <div className="login-container">
        <form className="login-form" onSubmit={handleLogin}>
          <h2 className="form-title">Sign in to your account</h2>

          <label>Username</label>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit" className="btn-primary">
            Sign In
          </button>

          <p className="register-text">
            Donâ€™t have an account? <Link to="/register">Create one</Link>
          </p>
        </form>
      </div>

      <div className="info-section">
        <div className="info-card">
          <h3>For HR Teams</h3>
          <p>Schedule interviews, manage candidates, and track progress effortlessly.</p>
        </div>
        <div className="info-card">
          <h3>For Interviewers</h3>
          <p>Access your upcoming interviews, share feedback, and collaborate seamlessly.</p>
        </div>
        <div className="info-card">
          <h3>For Candidates</h3>
          <p>View interview schedules, receive updates, and stay informed throughout the process.</p>
        </div>
      </div>
    </div>
  );
}
